#!/usr/bin/env python

from pwn import *
import re
import numpy
import codecs

host, port = '2018shell3.picoctf.com', 40440
context.log_level = 'error'

s = remote(host, port)
q_patt = re.compile(r"q : ([0-9]+)")
p_patt = re.compile(r"p : ([0-9]+)")
n_patt = re.compile(r"n : ([0-9]+)")

s.recv()
prompt = s.recv().decode("utf-8")
q = int(q_patt.search(prompt).group(1))
p = int(p_patt.search(prompt).group(1))
s.sendline("y")
s.sendline(str(q*p))

s.recv()
prompt = s.recv().decode("utf-8")
p = int(p_patt.search(prompt).group(1))
n = int(n_patt.search(prompt).group(1))
s.sendline("y")
s.sendline(str(n//p))

s.sendline("n")

s.recv()
prompt = s.recv().decode("utf-8")
s.sendline("y")
s.sendline("6256003596")

s.recv()
prompt = s.recv().decode("utf-8")
s.sendline("y")
s.sendline("")

s.recv()
prompt = s.recv().decode("utf-8")
s.sendline("n")


s.recv()
prompt = s.recv().decode("utf-8")
s.sendline("y")
s.sendline("")

s.recv()
prompt = s.recv().decode("utf-8")
s.sendline("y")
s.sendline("240109877286251840533272915662757983981706320845661471802585807564915966910384301849411666983334013")

